mapper.update:
update tb_card
   set user_no = #{userNo}
     , card_content = #{cardContent}
     , group_header_no = #{groupHeaderNo}
     , file_group_no = null
 where card_no = #{cardNo}

update tb_card
  set card_content = #{cardContent}
where card_no = #{cardNo}
  and user_no = #{userNo}

mapper.select:
select c.card_no , c.card_comment_no , c.card_comment_content
, date_format(c.card_comment_reg_date, '%Y-%m-%d %H:%i:%s') as reg_date , u.user_no , u.user_nick_name , ui.user_img_path , ui.user_img_no from tb_card_comment c, tb_user u , tb_user_img ui where c.user_no = u.user_no
and u.user_img_no = ui.user_img_no and c.card_no= #{cardNo}

mapper.insert:
insert into tb_card_comment(
	 card_no,
	 user_no,
	 card_comment_content

    )values(
	#{cardNo},
	#{userNo},
	#{cardCommentContent}

)

mapper.update:
update tb_card_comment
   set card_comment_content = #{cardCommentContent}
 where card_comment_no = #{cardCommentNo}
