mapper.select:
select user_email
  from tb_user
 where user_email = #{userEmail}

select user_nick_name
  from tb_user
 where user_nick_name = #{userNickName}

select user_join_no
  from tb_user
 where user_email = #{userEmail}

mapper.update:
		update tb_user
	       set user_conf = 'Y'
         where user_email = #{userEmail}

mapper.select:
select user_no, user_email, user_psw, user_nick_name, user_status, user_conf
  from tb_user
 where user_email = #{userEmail}
   and user_psw = #{userPsw}
   and user_status = 0
   and user_conf = 'Y'

mapper.update:
update tb_user
   set user_status = 1
 where user_email = #{userEmail}
